     2→import { View, StyleSheet } from 'react-native';
     3→import { StatusBar } from 'expo-status-bar';
     4→import { SafeAreaProvider } from 'react-native-safe-area-context';
     5→import { GestureHandlerRootView } from 'react-native-gesture-handler';
     6→import { PaperProvider, Appbar } from 'react-native-paper';
     7→import { lightTheme } from './src/theme/theme';
     8→
     9→// Simple navigation context (no react-native-screens)
    10→type Screen =
    11→  | { name: 'TemplateList' }
    12→  | { name: 'TemplateDetail'; params: { templateId: string } }
    13→  | { name: 'ActiveWorkout'; params: { templateId: string } }
    14→  | { name: 'WorkoutComparison'; params: { workoutId: string; templateId: string } };
    15→
    16→interface NavigationContextType {
    17→  screen: Screen;
    18→  navigate: (screen: Screen) => void;
    19→  goBack: () => void;
    20→  canGoBack: boolean;
    21→}
    22→
    23→const NavigationContext = createContext<NavigationContextType | null>(null);
    24→
    25→export function useNavigation() {
    26→  const ctx = useContext(NavigationContext);
    27→  if (!ctx) throw new Error('useNavigation must be used within NavigationProvider');
    28→  return ctx;
    29→}
    30→
    31→// Screens
    32→import TemplateListScreen from './src/screens/TemplateListScreen';
    33→import TemplateDetailScreen from './src/screens/TemplateDetailScreen';
    34→import ActiveWorkoutScreen from './src/screens/ActiveWorkoutScreen';
    35→import WorkoutComparisonScreen from './src/screens/WorkoutComparisonScreen';
    36→
    37→function getTitle(screen: Screen): string {
    38→  switch (screen.name) {
    39→    case 'TemplateList': return 'Workouts';
    40→    case 'TemplateDetail': return 'Workout Details';
    41→    case 'ActiveWorkout': return 'Active Workout';
    42→    case 'WorkoutComparison': return 'Workout Complete';
    43→  }
    44→}
    45→
    46→function AppContent() {
    47→  const [history, setHistory] = useState<Screen[]>([{ name: 'TemplateList' }]);
    48→  const screen = history[history.length - 1];
    49→
    50→  const navigate = (newScreen: Screen) => {
    51→    setHistory(prev => [...prev, newScreen]);
    52→  };
    53→
    54→  const goBack = () => {
    55→    if (history.length > 1) {
    56→      setHistory(prev => prev.slice(0, -1));
    57→    }
    58→  };
    59→
    60→  const canGoBack = history.length > 1 &&
    61→    screen.name !== 'ActiveWorkout' &&
    62→    screen.name !== 'WorkoutComparison';
    63→
    64→  const renderScreen = () => {
    65→    switch (screen.name) {
    66→      case 'TemplateList':
    67→        return <TemplateListScreen />;
    68→      case 'TemplateDetail':
    69→        return <TemplateDetailScreen templateId={screen.params.templateId} />;
    70→      case 'ActiveWorkout':
    71→        return <ActiveWorkoutScreen templateId={screen.params.templateId} />;
    72→      case 'WorkoutComparison':
    73→        return (
    74→          <WorkoutComparisonScreen
    75→            workoutId={screen.params.workoutId}
    76→            templateId={screen.params.templateId}
    77→          />
    78→        );
    79→    }
    80→  };
    81→
    82→  return (
    83→    <NavigationContext.Provider value={{ screen, navigate, goBack, canGoBack }}>
    84→      <View style={styles.container}>
    85→        <Appbar.Header mode="small" elevated>
    86→          {canGoBack && <Appbar.BackAction onPress={goBack} />}
    87→          <Appbar.Content title={getTitle(screen)} />
    88→        </Appbar.Header>
    89→        <View style={styles.content}>
    90→          {renderScreen()}
    91→        </View>
    92→      </View>
    93→    </NavigationContext.Provider>
    94→  );
    95→}
    96→
    97→export default function App() {
    98→  return (
    99→    <GestureHandlerRootView style={styles.flex}>
   100→      <SafeAreaProvider>
   101→        <PaperProvider theme={lightTheme}>
   102→          <StatusBar style="auto" />
   103→          <AppContent />
   104→        </PaperProvider>
   105→      </SafeAreaProvider>
   106→    </GestureHandlerRootView>
   107→  );
   108→}
   109→
   110→const styles = StyleSheet.create({
   111→  flex: {
   112→    flex: 1,
   113→  },
   114→  container: {
   115→    flex: 1,
   116→    backgroundColor: '#F2F2F7',
   117→  },
   118→  content: {
   119→    flex: 1,
   120→  },
   121→});
