  },
  editExerciseRow: {
    paddingVertical: 12,
  },
  exerciseNameInput: {
    fontSize: 16,
    fontWeight: '500',
    padding: 8,
    backgroundColor: '#F5F5F5',
    borderRadius: 6,
    marginBottom: 8,
  },
  editSets: {
    marginLeft: 8,
  },
  editSetRow: {
    flexDirection: 'row',
    alignItems: 'center',
    marginVertical: 4,
  },
  setLabel: {
    width: 50,
    opacity: 0.6,
  },
