  bottomBar: {
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
    padding: 16,
    paddingBottom: 32,
  },
  buttonRow: {
    flexDirection: 'row',
    gap: 12,
  },
  halfButton: {
    flex: 1,
    borderRadius: 12,
  },
  buttonContent: {
    paddingVertical: 8,
  },
  modal: {
    margin: 20,
    padding: 20,
    borderRadius: 12,
  },
  modalTitle: {
    fontWeight: '700',
    marginBottom: 12,
  },
  modalNotes: {
    opacity: 0.7,
    fontStyle: 'italic',
    marginBottom: 16,
  },
  modalSectionTitle: {
    opacity: 0.6,
    textTransform: 'uppercase',
    marginBottom: 8,
  },
